import React, { useState } from "react";
import "./App.css";

const weatherIcons = {
  0: "‚òÄÔ∏è",
  1: "üå§Ô∏è",
  2: "‚õÖ",
  3: "‚òÅÔ∏è",
  45: "üå´Ô∏è",
  48: "üå´Ô∏è",
  51: "üå¶Ô∏è",
  53: "üå¶Ô∏è",
  55: "üåßÔ∏è",
  56: "üå¶Ô∏è",
  57: "üåßÔ∏è",
  61: "üåßÔ∏è",
  63: "üåßÔ∏è",
  65: "üåßÔ∏è",
  66: "üåßÔ∏è",
  67: "üåßÔ∏è",
  71: "‚ùÑÔ∏è",
  73: "‚ùÑÔ∏è",
  75: "‚ùÑÔ∏è",
  77: "‚ùÑÔ∏è",
  80: "üåßÔ∏è",
  81: "üåßÔ∏è",
  82: "üåßÔ∏è",
  85: "‚ùÑÔ∏è",
  86: "‚ùÑÔ∏è",
  95: "‚õàÔ∏è",
  96: "‚õàÔ∏è",
  99: "‚õàÔ∏è",
};

function App() {
  const [city, setCity] = useState("");
  const [cityName, setCityName] = useState("");
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState([]);
  const [hourly, setHourly] = useState([]);
  const [selectedDay, setSelectedDay] = useState("");

  const fetchWeather = async (lat, lon, name) => {
    const url = `https://api.open-meteo.com/v1/forecast?latitude=${lat}&longitude=${lon}&current_weather=true&hourly=temperature_2m,weathercode&daily=temperature_2m_max,temperature_2m_min,weathercode&timezone=auto`;
    const res = await fetch(url);
    const data = await res.json();

    setCityName(name);
    setWeather(data.current_weather);

    // Prepare 5-day forecast starting from today
    const dailyForecast = data.daily.time.map((date, idx) => ({
      date,
      max: data.daily.temperature_2m_max[idx],
      min: data.daily.temperature_2m_min[idx],
      code: data.daily.weathercode[idx],
    })).slice(0, 5);
    setForecast(dailyForecast);

    // Hourly forecast
    const hourlyForecast = data.hourly.time.map((t, i) => ({
      time: t,
      temp: data.hourly.temperature_2m[i],
      code: data.hourly.weathercode[i],
    }));
    setHourly(hourlyForecast);

    setSelectedDay(dailyForecast[0].date); // default today
  };

  const handleSearch = async () => {
    if (!city) return;
    const geoUrl = `https://geocoding-api.open-meteo.com/v1/search?name=${city}&count=1`;
    const res = await fetch(geoUrl);
    const data = await res.json();
    if (data.results && data.results.length > 0) {
      const { latitude, longitude, name } = data.results[0];
      fetchWeather(latitude, longitude, name);
    }
  };

  const handleCurrentLocation = () => {
    navigator.geolocation.getCurrentPosition((pos) => {
      fetchWeather(pos.coords.latitude, pos.coords.longitude, "My Location");
    });
  };

  const getDayName = (dateStr) => {
    const d = new Date(dateStr);
    return d.toLocaleDateString("en-US", { weekday: "short", month: "short", day: "numeric" });
  };

  const getHourlyForDay = (dateStr) => {
    return hourly.filter((h) => h.time.startsWith(dateStr));
  };

  const getBackgroundClass = () => {
    if (!weather) return "sunny";
    const code = weather.weathercode;
    if (code >= 61 && code <= 82) return "rainy";
    if (code >= 3 && code <= 48) return "cloudy";
    const hour = new Date().getHours();
    if (hour >= 18 || hour < 6) return "night";
    return "sunny";
  };

  return (
    <div className={`app ${getBackgroundClass()}`}>
      <h1>üå§ Weather App {cityName && `in ${cityName}`}</h1>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Enter city"
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
        <button onClick={handleCurrentLocation}>üìç My Location</button>
      </div>

      <div className="forecast-hourly-container">
        <div className="left-panel">
          {forecast.map((day) => (
            <div
              key={day.date}
              className={`forecast-card ${selectedDay === day.date ? "selected" : ""}`}
              onClick={() => setSelectedDay(day.date)}
            >
              <p>{getDayName(day.date)}</p>
              <div style={{ fontSize: "1.5rem" }}>{weatherIcons[day.code] || "‚ùî"}</div>
              <p>
                {day.min}¬∞C / {day.max}¬∞C
              </p>
            </div>
          ))}
        </div>

        <div className="right-panel">
          {selectedDay && (
            <>
              <h2>Hourly Forecast for {getDayName(selectedDay)}</h2>
              <div className="hourly-scroll">
                {getHourlyForDay(selectedDay).map((h, idx) => (
                  <div key={idx} className="hourly-card">
                    <p>{new Date(h.time).getHours()}:00</p>
                    <div style={{ fontSize: "1.5rem" }}>{weatherIcons[h.code] || "‚ùî"}</div>
                    <p>{h.temp}¬∞C</p>
                  </div>
                ))}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
