import React, { useState } from 'react';
import './App.css';

const weatherIcons = {
  0: '☀️', 1: '🌤️', 2: '⛅', 3: '☁️',
  45: '🌫️', 48: '🌫️',
  51: '🌦️', 53: '🌦️', 55: '🌧️',
  56: '🌦️', 57: '🌧️',
  61: '🌧️', 63: '🌧️', 65: '🌧️',
  66: '🌧️', 67: '🌧️',
  71: '❄️', 73: '❄️', 75: '❄️', 77: '❄️',
  80: '🌧️', 81: '🌧️', 82: '🌧️',
  85: '❄️', 86: '❄️',
  95: '⛈️', 96: '⛈️', 99: '⛈️'
};

function App() {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState([]);
  const [cityName, setCityName] = useState('');
  const [hourly, setHourly] = useState({ time: [], temperature: [], code: [] });
  const [selectedDay, setSelectedDay] = useState(null);

  const fetchWeather = async (lat, lon, name = '') => {
    const url = `https://api.open-meteo.com/v1/forecast?latitude=${lat}&longitude=${lon}&current_weather=true&hourly=temperature_2m,weathercode&daily=temperature_2m_max,temperature_2m_min,weathercode&timezone=auto`;
    const res = await fetch(url);
    const data = await res.json();

    setWeather(data.current_weather);
    setForecast({
      time: data.daily.time.slice(0, 5), // today + next 4 days
      temperature_2m_max: data.daily.temperature_2m_max.slice(0, 5),
      temperature_2m_min: data.daily.temperature_2m_min.slice(0, 5),
      weathercode: data.daily.weathercode.slice(0, 5)
    });
    setCityName(name);
    setHourly({
      time: data.hourly.time,
      temperature: data.hourly.temperature_2m,
      code: data.hourly.weathercode
    });
    setSelectedDay(data.daily.time[0]); // default selected day = today
  };

  const handleSearch = async () => {
    if (!city) return;
    const geoUrl = `https://geocoding-api.open-meteo.com/v1/search?name=${city}&count=1`;
    const res = await fetch(geoUrl);
    const data = await res.json();
    if (data.results && data.results.length > 0) {
      const { latitude, longitude, name } = data.results[0];
      fetchWeather(latitude, longitude, name);
    }
  };

  const handleCurrentLocation = () => {
    navigator.geolocation.getCurrentPosition((pos) => {
      fetchWeather(pos.coords.latitude, pos.coords.longitude, 'My Location');
    });
  };

  const getDayName = (dateStr) => {
    const d = new Date(dateStr);
    return d.toLocaleDateString('en-US', { weekday: 'short', month: 'short', day: 'numeric' });
  };

  const getHourlyForDay = (dateStr) => {
    return hourly.time
      .map((t, i) => ({ time: t, temp: hourly.temperature[i], code: hourly.code[i] }))
      .filter((h) => h.time.startsWith(dateStr));
  };

  // Dynamic background based on current weather
  let backgroundClass = 'sunny';
  if (weather) {
    if (weather.weathercode >= 61 && weather.weathercode <= 82) backgroundClass = 'rainy';
    else if (weather.weathercode >= 3 && weather.weathercode <= 48) backgroundClass = 'cloudy';
    else if (new Date().getHours() >= 18 || new Date().getHours() < 6) backgroundClass = 'night';
  }

  return (
    <div className={`app ${backgroundClass}`}>
      <h1>🌤 Weather App {cityName && `in ${cityName}`}</h1>

      <div className="search-bar">
        <input 
          type="text" 
          placeholder="Enter city" 
          value={city} 
          onChange={(e) => setCity(e.target.value)} 
        />
        <button onClick={handleSearch}>Search</button>
        <button onClick={handleCurrentLocation}>📍 My Location</button>
      </div>

      <div className="forecast-hourly-container">
        <div className="left-panel">
          {forecast.time && forecast.time.map((day, idx) => (
            <div 
              key={day} 
              className={`forecast-card ${selectedDay === day ? 'selected' : ''}`}
              onClick={() => setSelectedDay(day)}
            >
              <p>{getDayName(day)}</p>
              <div style={{ fontSize: '1.5rem' }}>{weatherIcons[forecast.weathercode[idx]] || '❔'}</div>
              <p>{forecast.temperature_2m_min[idx]}°C / {forecast.temperature_2m_max[idx]}°C</p>
            </div>
          ))}
        </div>

        <div className="right-panel">
          {selectedDay && (
            <>
              <h2>Hourly Forecast for {getDayName(selectedDay)}</h2>
              <div className="hourly-scroll">
                {getHourlyForDay(selectedDay).map((h, idx) => (
                  <div key={idx} className="hourly-card">
                    <p>{new Date(h.time).getHours()}:00</p>
                    <div style={{ fontSize: '1.5rem' }}>{weatherIcons[h.code] || '❔'}</div>
                    <p>{h.temp}°C</p>
                  </div>
                ))}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
