import React, { useState } from 'react';

const weatherIcons = {
  0: '‚òÄÔ∏è',   // Clear sky
  1: 'üå§Ô∏è',  // Mainly clear
  2: '‚õÖ',   // Partly cloudy
  3: '‚òÅÔ∏è',   // Overcast
  45: 'üå´Ô∏è',  // Fog
  48: 'üå´Ô∏è',  // Depositing rime fog
  51: 'üå¶Ô∏è',  // Light drizzle
  53: 'üå¶Ô∏è',  // Moderate drizzle
  55: 'üåßÔ∏è',  // Dense drizzle
  56: 'üå¶Ô∏è',  // Light freezing drizzle
  57: 'üåßÔ∏è',  // Dense freezing drizzle
  61: 'üåßÔ∏è',  // Slight rain
  63: 'üåßÔ∏è',  // Moderate rain
  65: 'üåßÔ∏è',  // Heavy rain
  66: 'üåßÔ∏è',  // Light freezing rain
  67: 'üåßÔ∏è',  // Heavy freezing rain
  71: '‚ùÑÔ∏è',  // Slight snow fall
  73: '‚ùÑÔ∏è',  // Moderate snow fall
  75: '‚ùÑÔ∏è',  // Heavy snow fall
  77: '‚ùÑÔ∏è',  // Snow grains
  80: 'üåßÔ∏è',  // Rain showers: slight
  81: 'üåßÔ∏è',  // Rain showers: moderate
  82: 'üåßÔ∏è',  // Rain showers: violent
  85: '‚ùÑÔ∏è',  // Snow showers: slight
  86: '‚ùÑÔ∏è',  // Snow showers: heavy
  95: '‚õàÔ∏è',  // Thunderstorm: slight or moderate
  96: '‚õàÔ∏è',  // Thunderstorm with slight hail
  99: '‚õàÔ∏è'   // Thunderstorm with heavy hail
};

function App() {
  const [city, setCity] = useState('');
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState([]);
  const [cityName, setCityName] = useState('');

  const fetchWeather = async (lat, lon, name = '') => {
    const url = `https://api.open-meteo.com/v1/forecast?latitude=${lat}&longitude=${lon}&current_weather=true&daily=temperature_2m_max,temperature_2m_min,weathercode&timezone=auto`;
    const res = await fetch(url);
    const data = await res.json();
    setWeather(data.current_weather);
    setForecast(data.daily);
    setCityName(name);
  };

  const handleSearch = async () => {
    if (!city) return;
    const geoUrl = `https://geocoding-api.open-meteo.com/v1/search?name=${city}&count=1`;
    const res = await fetch(geoUrl);
    const data = await res.json();
    if (data.results && data.results.length > 0) {
      const { latitude, longitude, name } = data.results[0];
      fetchWeather(latitude, longitude, name);
    }
  };

  const handleCurrentLocation = () => {
    navigator.geolocation.getCurrentPosition((pos) => {
      fetchWeather(pos.coords.latitude, pos.coords.longitude, 'My Location');
    });
  };

  return (
    <div className="app">
      <h1>üå§ Weather App</h1>
      <div>
        <input 
          type="text" 
          placeholder="Enter city" 
          value={city} 
          onChange={(e) => setCity(e.target.value)} 
        />
        <button onClick={handleSearch}>Search</button>
        <button onClick={handleCurrentLocation}>üìç My Location</button>
      </div>

      {weather && (
        <div className="weather-card">
          <h2>Current Weather {cityName && `in ${cityName}`}</h2>
          <div style={{ fontSize: '2rem' }}>
            {weatherIcons[weather.weathercode] || '‚ùî'}
          </div>
          <p>Temperature: {weather.temperature}¬∞C</p>
          <p>Windspeed: {weather.windspeed} km/h</p>
        </div>
      )}

      {forecast.time && (
        <div>
          <h2>5-Day Forecast</h2>
          <div className="forecast-container">
            {forecast.time.slice(1, 6).map((day, idx) => (
              <div key={day} className="forecast-card">
                <p>{day}</p>
                <div style={{ fontSize: '1.5rem' }}>
                  {weatherIcons[forecast.weathercode[idx + 1]] || '‚ùî'}
                </div>
                <p>
                  {forecast.temperature_2m_min[idx + 1]}¬∞C / {forecast.temperature_2m_max[idx + 1]}¬∞C
                </p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
